modelw2v = Word2Vec.load("./modeltrain/model.bin")
modelw2v.init_sims(replace=True)

path = "./data/test1"


def vectorize(doc):
            doc = [word for word in doc if word in modelw2v.wv.vocab]
            if len(doc) == 0:
                return None
            return np.mean(modelw2v.wv.__getitem__(doc), axis=0)
def _cosine_sim(vecA, vecB):
        """Find the cosine similarity distance between two vectors."""
        csim = np.dot(vecA, vecB) / (np.linalg.norm(vecA) * np.linalg.norm(vecB))
        if np.isnan(np.sum(csim)):
            return 0
        return csim


dictionary = corpora.Dictionary.load("./modeltrain/diclda.bin")

def ldacp(sent1,sent2):
    #print(sent1)
    sent11 = []
    sent11.append(sent1.split())
    sent11 = sent11[0]

    sent21 = []
    sent21.append(sent2.split())
    sent21 = sent21[0]
    # print(sent11)

    vec11 = dictionary.doc2bow(sent11)
    vec21 = dictionary.doc2bow(sent21)
    ldavec11 = ldabow[vec11]
    ldavec21 = ldabow[vec21]
    similar = gensim.matutils.cossim(ldavec11,ldavec21)
    return similar



def w2vcp(sent1, sent2):
    sent11 = []
    sent11.append(sent1.split())
    sent11 = sent11[0]
    sent21 = []
    sent21.append(sent2.split())
    sent21 = sent21[0]

    vec11 = vectorize(sent11)
    #print(vec11)
    vec21 = vectorize(sent21)

    if vec11 is None or vec21 is None:
        return 0.0
    similar = _cosine_sim(vec11,vec21)
    return similar


def compair(sent1, sent2):
    sent1 = preprocess(sent1)
    sent2 = preprocess(sent2)
    if sent1.strip() == '' or sent2.strip() == '':
        return 0.0
    a = ldacp(sent1, sent2)
    b = w2vcp(sent1, sent2)
    return (a+b)/2


